import React, { Component } from "react";
import "../post.css";

class Post extends Component {
  constructor() {
    super();
    this.state = {
      likes: 0
    };
  }

  render() {
    return (
      <div class={"post"}>
        <h3>{this.props.children}</h3>

        <small>{this.props.time}</small>
        <div style={likeLine}>
          <p>Like:{this.state.likes}</p>
          <button
            //() => --e uma função
            onClick={() => this.setState({ likes: this.state.likes + 1 })}
            style={{
              backgroundColor: "blue",
              color: "white",
              fontSize: 16,
              fontWeight: "bolder",
              borderRadius: 10,
              padding: 5
            }}
          >
            Like
          </button>
        </div>
      </div>
    );
  }
}
export default Post;

const likeLine = {
  display: "flex"
};
