import React, { Component } from "react";

class PostCreator extends Component {
  constructor() {
    super();
    this.state = {
      text: ""
    };
  }
  render() {
    return (
      <div>
        <h3>Novo Post</h3>
        <input
          onChange={event => {
            const value = event.target.value;
            this.setState({ teste: value });
            console.log(event);
          }}
          value={this.state.text}
          style={{ width: "100%" }}
        >
          aaa
        </input>
        <button>Postar</button>
      </div>
    );
  }
}

export default PostCreator;
