import React, { Components } from "react";

class PostCreator extends Components {
  constructor() {
    super();
    this.state = {
      text: ""
    };
  }
  render() {
    return (
      <div>
        <h3>Novo Post</h3>
        <input
          onchange={event => {
            console.log(event);
          }}
          value={this.state.text}
          style={{ width: "100%" }}
        />
        <button>Postar</button>
      </div>
    );
  }
}

export default PostCreator;
