import React, { Component } from "react";

import Post from "./post.js";
import PostCreator from "./postCreator.js";
import Head from "./head";

const postArray = [
  {
    id: 1,
    content: "Primeiro Post",
    time: "16:87",
    autor: "Baraky",
    initialLikes: 0
  },
  {
    id: 2,
    content: "Segundo Post",
    time: "12:55",
    autor: "Lemos",
    initialLikes: 1
  }
];
class Timeline extends Component {
  constructor() {
    super();
    this.state = {
      posts: postArray,
      selectedOption: null
    };
  }

  componentDidMount() {
    console.log("app did mouth");
    this.readFromStorange();
  }

  insertPost(post) {
    const myPosts = this.state.posts;
    //myPosts.push(post);
    myPosts.unshift(post);
    this.setState({ posts: myPosts });
    this.saveInStorange();
  }

  saveInStorange() {
    const posts = JSON.stringify(this.state.posts);
    localStorage.setItem("savedPosts", posts);
  }

  readFromStorange() {
    const savedPosts = localStorage.getItem("savedPosts");
    if (savedPosts) {
      this.setState({ posts: JSON.parse(savedPosts) });
    }
  }

  onNavigate(post) {
    this.props.history.push("/post/" + post.time);
  }

  handleChange = selectedOption => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  };
  render() {
    const { selectedOption } = this.state;
    return (
      <div>
        <Head props={this.props} />

        <PostCreator onCreate={this.insertPost.bind(this)} />

        {this.state.posts.map((post, i) => {
          return (
            <Post
              onNavigate={() => this.onNavigate(post)}
              key={post.time}
              post={post}
            />
          );
        })}
      </div>
    );
  }
}

export default Timeline;

