import React, { Component } from "react";

import Post from "./post.js";
import PostCreator from "./postCreator.js";
import Head from "./head";
import Select from "react-select";

const postArray = [
  {
    id: 1,
    content: "Primeiro Post",
    time: "16:87",
    autor: 1,
    initialLikes: 0
  },
  {
    id: 2,
    content: "Segundo Post",
    time: "12:55",
    autor: 2,
    initialLikes: 1
  }
];

const options = [
  { value: "1", label: "André " },
  { value: "2", label: "Lucas" },
  { value: "3", label: "Pedro" }
];
class Timeline extends Component {
  constructor() {
    super();
    this.state = {
      posts: postArray,
      autorAtual: 1
    };
  }

  componentDidMount() {
    console.log("app did mouth");
    this.readFromStorange();
  }

  insertPost(post) {
    const myPosts = this.state.posts;
    //myPosts.push(post);
    myPosts.unshift(post);
    this.setState({ posts: myPosts });
    this.saveInStorange();
  }

  saveInStorange() {
    const posts = JSON.stringify(this.state.posts);
    localStorage.setItem("savedPosts", posts);
  }

  readFromStorange() {
    const savedPosts = localStorage.getItem("savedPosts");
    if (savedPosts) {
      this.setState({ posts: JSON.parse(savedPosts) });
    }
  }
  filtraPosts(autor) {
    console.log(autor);
  }

  onNavigate(post) {
    this.props.history.push("/post/" + post.time);
  }

  handleChange(selectedOption) {
    console.log(">>>>>", this.state.autorAtual);
  }
  render() {
    return (
      <div>
        <Head props={this.props} />
        <h2>Selecione o Usuario:</h2>
        <Select
          value={this.state.autorAtual}
          onChange={event => {
            const value = event.value;
            this.setState({ autorAtual: value }, () => {
              console.log("doLike state", this.state);
              this.handleChange();
            });
          }}
          options={options}
        />

        <PostCreator
          autorAtual={this.state.autorAtual}
          onCreate={this.insertPost.bind(this)}
        />

        {this.state.posts.map((post, i) => {
          return (
            <Post
              onNavigate={() => this.onNavigate(post)}
              key={post.time}
              post={post}
            />
          );
        })}
      </div>
    );
  }
}

export default Timeline;

