import React, { Component } from "react";
import { CircularProgress } from "@material-ui/core";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import SaveIcon from "@material-ui/icons/Save";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import Select from "react-select";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import ReactDOM from "react-dom";
import NativeSelect from "@material-ui/core/NativeSelect";

class PostCreator extends Component {
  constructor() {
    super();
    this.state = {
      text: "",
      users: [],
      idUserAtual: -1
    };
  }
  componentDidMount() {
    this.readUsers();
  }
  createPost(author) {
    console.log("8888", author);
    axios.get("http://localhost:3001/author/" + author).then(response => {
      const newPost = {
        content: this.state.text,
        author: author,
        imageAuthor: response.data.image,
        time: new Date().getTime(),
        initialLikes: 0
      };
      this.props.onCreate(newPost);
    });
  }

  readUsers() {
    axios.get("http://localhost:3001/author").then(response => {
      this.setState({ users: response.data });
    });
  }
  handleChange(value7) {
    console.log("asdfasdfasdf4444", this.state.idUserAtual, value7);
  }
  render() {
    console.table(this.state.users);
    return (
      <div style={{ padding: 15 }}>
        <h3>Crie seu Post</h3>
        <FormControl style={{ width: "100%" }}>
          <NativeSelect
            native
            value={this.state.idUserAtual}
            onChange={() => {
              this.handleChange(value);
            }}
            inputProps={{
              name: "age",
              id: "age-native-simple"
            }}
          >
            <option style={{ color: "#b1b4bc" }} value={""}>
              Selecione um Usuário
            </option>
            <option style={{ color: "#b1b4bc" }} value={2}>
              Selecione um Usuário
            </option>
            {this.state.users.map((user, i) => {
              return <option value={user.id}>{user.name}</option>;
            })}
          </NativeSelect>
        </FormControl>
        <TextField
          variant="filled"
          defaultValue="Default Value"
          multiline
          id="filled-multiline-static"
          label="Titulo"
          rows="4"
          onChange={event => {
            const value = event.target.value;
            this.setState({ text: value });
          }}
          value={this.state.text}
          style={{ width: "100%" }}
        />
        {this.props.isLoading ? (
          <CircularProgress />
        ) : (
          <Button
            style={{ margin: 10 }}
            onClick={() => this.createPost(this.props.authorAtual)}
            variant="contained"
            color="primary"
          >
            save
            {/* This Button uses a Font Icon, see the installation instructions in the docs. */}
            <SaveIcon />
          </Button>
        )}
      </div>
    );
  }
}

export default PostCreator;
