import axios from "axios";

class DAO {
  static addPost(post) {
    axios.post("http://localhost:3001/posts", post);
  }
  static getPosts() {
    axios.get("http://localhost:3001/posts");
  }
  static getPost(id) {
    axios.get("http://localhost:3001/posts/" + id);
  }
  /////////////////////////////////////////////////////////////////
  static getUser(id) {
    axios.get("http://localhost:3001/author/" + id).then(response => {
      console.log("asdfasdfqqqq", response.data);
      return response.data;
    });
  }
}
export default DAO;
