import axios from "axios";

class DAO {
  static addPost(post) {
    axios.post("http://localhost:3001/posts", post);
  }
  static getPosts() {
    const posts = axios.get("http://localhost:3001/posts/").then(response => {
      return response.data;
    });
    return posts;
  }
  static getPost(id) {
    const post = axios
      .get("http://localhost:3001/posts/" + id)
      .then(response => {
        return response.data;
      });
    return post;
  }
  /////////////////////////////////////////////////////////////////
  static getUser(id) {
    const user = axios
      .get("http://localhost:3001/author/" + id)
      .then(response => {
        return response.data;
      });
    return user;
  }
  static getUsers() {
    const user = axios.get("http://localhost:3001/author").then(response => {
      return response.data;
    });
    return user;
  }
}
export default DAO;
