import React, { Component } from "react";

class Storange extends Component {
  getPost(id) {
    const posts = JSON.parse(localStorage.getItem("savedPosts"));
    const post = posts.filter(savedPost => {
      return savedPost.time == id;
    })[0];
    return post;
  }

  getPosts() {
    return JSON.parse(localStorage.getItem("savedPosts"));
  }

  savePost(post) {
    const Posts = JSON.parse(localStorage.getItem("savedPosts"));
    Posts.unshift(post);
    localStorage.setItem("savedPosts", JSON.stringify(Posts));
  }

  savePosts(posts) {
    localStorage.setItem("savedPosts", JSON.stringify(posts));
  }

  setPost(id) {}
}
export default Storange;
