import React, { Component } from "react";

import Post from "./components/post.js";
import PostCreator from "./components/postCreator.js";

const postArray = [
  {
    id: 1,
    content: "Primeiro Post",
    time: "16:87",
    autor: "Baraky",
    initialLikes: 0
  },
  {
    id: 2,
    content: "Segundo Post",
    time: "12:55",
    autor: "Lemos",
    initialLikes: 1
  }
];
class App extends Component {
  constructor() {
    super();
    this.state = {
      posts: postArray
    };
  }

  componentDidMount() {
    console.log("app did mouth");
    this.readFromStorange();
  }

  insertPost(post) {
    const myPosts = this.state.posts;
    //myPosts.push(post);
    myPosts.unshift(post);
    this.setState({ posts: myPosts });
    this.saveInStorange();
  }

  saveInStorange() {
    const posts = JSON.stringify(this.state.posts);
    localStorage.setItem("savedPosts", posts);
    console.table("savedPosts");
  }

  readFromStorange() {
    const savedPosts = localStorage.getItem("savedPosts");
    if (savedPosts) {
      this.setState({ posts: JSON.parse(savedPosts) });
    }
  }

  render() {
    return (
      <div>
        <h1>Minha rede social</h1>
        <PostCreator onCreate={this.insertPost.bind(this)} />
        {this.state.posts.map((post, i) => {
          return <Post key={post.time} post={post} />;
        })}
      </div>
    );
  }
}

export default App;

