import React, { Component } from "react";
import { CircularProgress } from "@material-ui/core";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import SaveIcon from "@material-ui/icons/Save";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import Select from "react-select";

class PostCreator extends Component {
  constructor() {
    super();
    this.state = {
      text: ""
    };
  }

  createPost(author) {
    console.log("8888", author);
    axios.get("http://localhost:3001/author/" + author).then(response => {
      const newPost = {
        content: this.state.text,
        author: author,
        imageAuthor: response.data.image,
        time: new Date().getTime(),
        initialLikes: 0
      };
      this.props.onCreate(newPost);
    });
  }

  render() {
    return (
      <div style={{ padding: 15 }}>
        <h3>Crie seu Post</h3>
        <Select
          label
          value={this.authorAtual}
          onChange={event => {
            const value = event.value;
            this.setState({ authorAtual: value }, () => {
              console.log("doLike state", this.state);
              this.handleChange();
            });
          }}
          options={this.state.options}
        />
        <TextField
          variant="filled"
          defaultValue="Default Value"
          multiline
          id="filled-multiline-static"
          label="Titulo"
          rows="4"
          onChange={event => {
            const value = event.target.value;
            this.setState({ text: value });
          }}
          value={this.state.text}
          style={{ width: "100%" }}
        />
        {this.props.isLoading ? (
          <CircularProgress />
        ) : (
          <Button
            style={{ margin: 10 }}
            onClick={() => this.createPost(this.props.authorAtual)}
            variant="contained"
            color="primary"
          >
            save
            {/* This Button uses a Font Icon, see the installation instructions in the docs. */}
            <SaveIcon />
          </Button>
        )}
      </div>
    );
  }
}

export default PostCreator;
