import React, { Component } from "react";

import Post from "./components/post.js";
import PostCreator from "./components/postCreator.js";

const postArray = [
  {
    id: 1,
    content: "Primeiro Post",
    time: "16:87",
    autor: "Baraky",
    initialLikes: 0
  },
  {
    id: 2,
    content: "Segundo Post",
    time: "12:55",
    autor: "Lemos",
    initialLikes: 1
  }
];
class App extends Component {
  constructor() {
    super();
    this.state = {
      posts: postArray
    };
  }

  render() {
    return (
      <div>
        <PostCreator />
        <h1>Minha rede social</h1>

        {postArray.map(post => {
          return (
            <Post
              key={post.id}
              post={post}
              // autor={post.autor}
              // initialLikes={post.initialLikes}
              // time={post.time}
            />
          );
        })}
      </div>
    );
  }
}

export default App;

